import { Component } from '@angular/core';
import { Residence } from 'src/app/core/Models/Residence';

@Component({
  selector: 'app-resdidence-component',
  templateUrl: './resdidence-component.component.html',
  styleUrls: ['./resdidence-component.component.css']
})
export class ResdidenceComponentComponent {

  listFavoris:Residence[] | undefined;
  listResidences:Residence[]=[
    {id:1,"name": "El fel","address":"Borj Cedria",
    status: "Disponible"},
    {id:2,
    "name": "El yasmine",
    "address":"Ezzahra", status:
    "Disponible" },
    {id:3,
    "name": "El Arij",
    "address":"Rades",
    status:"Vendu"},
    {id:4,
    "name": "El Anber",
    "address":"inconnu",
    status: "En Construction"}
    ];
    location:string ="";
    id:number=0;
    nb:number=0;


    showLocation(address: string ){

      if(address==="inconnu")
          alert("résidence est inconnu");
        else
        this.location=address;

    }
    addResidenceFavoris(id: number ){
      if(this.listFavoris?.indexOf(this.listResidences[id])!=-1)
      {
        this.listFavoris?.push(this.listResidences[id])
        this.nb=this.nb+1;
      }

    else
    {
    this.listFavoris.splice(this.listFavoris?.indexOf(this.listResidences[id]),1)
    this.nb=this.nb-1;
  }


    }

}
